import Task from "../Task/Task";
import './Lane.css';

function Lane(props) {

  const { title, loading, error, tasks } = props

  return (
    <div className='Lane-wrapper'>
      <h2>{title}</h2>

      {loading || error? (

        <span>{error || 'Loading...'}</span>
      ): (

        tasks.map((task) => (

          <Task 
           key={task.id}
           id={task.id}
           title={task.title}
           body={task.body}
           />
        ))
      )}
    </div>
  );
}

export default Lane;
