          `https://my-json-server.typicode.com/PacktPublishing/React-Projects-Second-Edition/tasks`,
        );

        const result = await tasks.json();

        if (result) {

          setTasks(result);

          setLoading(false);
        }
      } catch (e) {

        setLoading(false);

        setError(e.message);
      }
    }

    fetchData();
  }, []);
  return (
    <div className='Board-wrapper'>
      {lanes.map((lane) => (
        <Lane 
